import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Loader from "../components/Loader";
import Error from "../components/Error";
import moment from "moment";

const Bookingscreen = () => {
  const [loading, setloading] = useState(true);
  const [error, seterror] = useState();
  const [room, setroom] = useState();

  // let { id } = useParams();
  const { id, fromdate, todate } = useParams();

  // const fromd = moment(fromdate, 'DD-MM-YYYY');
  // const tod = moment(todate, 'DD-MM-YYYY');
  const fromd = fromdate;
  const tod = todate;
  

  const totaldays = moment.duration(tod.diff(fromd)).asDays()+1;
  const [totalamount, settotalamount] = useState();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setloading(true);
        const data = (
          await axios.post("/api/rooms/getroombyid", { roomid: id })
        ).data;
        settotalamount(data.rentperday * totaldays);
        setroom(data);
        setloading(false);
        console.log(data);
      } catch (error) {
        seterror(true);
        console.log(error);
        setloading(false);
      }
    };
    fetchData();
  }, []);

  const bookroom = async ()=>{
    const bookingdetails = {
      room,
      userid:JSON.parse(localStorage.getItem('currentuser'))._id,
      fromdate,
      todate,
      totalamount,
      totaldays
    }

    try {
      const result = await axios.post('/api/bookings/bookroom', bookingdetails); 
    } catch (error) {
      
    }
  }

  return (
    <div className="m-5">
      {loading ? (
        (<Loader />)
      ): room ?(
        <div>
          <div className="row justify-content-center mt-5 bs">
            <div className="col-md-5">
              <h1>{room.name}</h1>
              <img src={room.imageurls[0]} className="bigimg" />
            </div>
            <div className="col-md-5" >
              <div style={{ textAlign: "right" }}>
                <h1>Booking Details</h1>
                <hr />
                <b>
                  <p>Name:</p>
                  <p>From Date: {fromdate}</p>
                  <p>To Date: {todate}</p>
                  <p>Max Count: {room.maxcount}</p>
                </b>
              </div>
              <div style={{ textAlign: "right" }}>
                <b>
                  <h1>Amount</h1>
                  <hr />
                  <p>Total days : {totaldays}</p>
                  <p>Rent per day : {room.rentperday}</p>
                  <p>Total Amount : {totalamount}</p>
                </b>
              </div>
              <div style={{ float: "right" }}>
                <button className="btn btn-primary" onClick = {bookroom}>Pay Now</button>
              </div>
            </div>
          </div>
        </div>
      ) : (
        (<Error />)
      )}
    </div>
  );
};

export default Bookingscreen;
