export function Users() {
  const [users, setusers] = useState([]);
  const [loading, setloading] = useState(true);
  const [error, seterror] = useState();

  const [updateState, setUpdateState] = useState(-1);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [show, setShow] = useState(false);

  useEffect(() => {
    async function getAllUsers() {
      try {
        const response = await axios.get("/api/users/getallusers");
        const data = response.data;
        setusers(data);
        setloading(false);
      } catch (error) {
        console.log(error);
        setloading(false);
        seterror(error);
      }
    }
    getAllUsers();
  }, []);

  function handleEdit(id) {
    setUpdateState(id);
  }
  function deleteUser(userid) {
    axios.delete(`/api/users/deleteuser/${userid}`)
      .then((response) => {
        console.log(response.data);
        // update the users list in your state
        Swal.fire("Congrats", "User is deleted successfully", "success").then(
          (result) => {
            setusers(users.filter(user => user._id !== userid));
          });
        

      })
      .catch((error) => {
        console.log(error);
        Swal.fire("Oops", "Something went wrong", "error");
      });
  }

  return (
    <div className="row">
      <div className="col-md-12">
        <h1>Users</h1>
        {loading && <Loader />}

        <table className="table table-bordered table table-hover table table-striped">
          <thead className="bs thead-dark">
            <tr>
              <th>Users Id</th>
              <th>Name</th>
              <th>Email</th>
              <th>Employee</th>
              <th>Admin</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.length &&
              users.map((user) => {
                return updateState === user._id ? (
                  <EditUser
                    user={user}
                    users={users}
                    setusers={setusers}
                    show={show}
                    handleClose={handleClose}
                  />
                ) : (
                  <tr>
                    <td>{user._id}</td>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.isEmployee ? "Yes" : "No"}</td>
                    <td>{user.isAdmin ? "Yes" : "No"}</td>
                    <td>
                      <button
                        class="btn btn-primary"
                        onClick={() => {
                          handleEdit(user._id);
                          handleShow();
                        }}
                      >
                        Update
                      </button>
                      <button class="btn btn-primary" onClick={() => deleteUser(user._id)}>Delete</button>
                    </td>
                    {/* <td>
                      <button class="btn btn-primary">Delete</button>
                    </td> */}
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>
    </div>
  );
}
export function EditUser({ user, users, setusers, show, handleClose }) {

  const [updateduser, setupdateduser] = useState({
    name: user.name,
    address: user.address,
    username: user.username,
    email: user.email,
    password: user.password,
    phonenumber: user.phonenumber,
    isEmployee: user.isEmployee
  });
  console.log(updateduser);

  const handleUpdateUser = () => {
    axios
      .put(
        `/api/users/updateuser/${user._id}`,
        updateduser
      )
      .then((response) => {
        // handle success
        console.log(response.data);
        handleClose();
        Swal.fire("Congrats", "User is updated successfully", "success").then(
          (result) => {
            window.location.href = "/admin";
          });
      })
      .catch((error) => {
        // handle error
        console.log(error);
        Swal.fire("Oops", "Something went wrong", "error");
      });
  };

  return (
    <Modal show={show} onHide={handleClose} animation={false} size="lg">
      <Modal.Header>
        <Modal.Title>Edit User Here</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        Name:
        <input
          type="text"
          className="form-control"
          placeholder="Name"
          name="name"
          value={updateduser.name}
          required
          // onChange={(e)=>{setupdateduser({name: e.target.value})}}
          onChange={(e) => {
            setupdateduser(prevState => ({
              ...prevState,
              [e.target.name]: e.target.value
            }));
          }}
        />
        Address:
        <input
          type="text"
          className="form-control"
          placeholder="Address"
          name="address"
          value={updateduser.address}
          required
          // onChange={(e)=>{setupdateduser({address: e.target.value})}}
          onChange={(e) => {
            setupdateduser(prevState => ({
              ...prevState,
              [e.target.name]: e.target.value
            }));
          }}
        />
        Username:
        <input
          type="text"
          className="form-control"
          placeholder="Username"
          name="username"
          value={updateduser.username}
          required
          // onChange={(e)=>{setupdateduser({username: e.target.value})}}
          onChange={(e) => {
            setupdateduser(prevState => ({
              ...prevState,
              [e.target.name]: e.target.value
            }));
          }}
        />
        Email:
        <input
          type="email"
          className="form-control"
          placeholder="Email"
          name="email"
          value={updateduser.email}
          required
          // onChange={(e)=>{setupdateduser({email: e.target.value})}}
          onChange={(e) => {
            setupdateduser(prevState => ({
              ...prevState,
              [e.target.name]: e.target.value
            }));
          }}
        />
        Password:
        <input
          type="password"
          className="form-control"
          placeholder="Password"
          name="password"
          value={updateduser.password}
          required
          // onChange={(e)=>{setupdateduser({password: e.target.value})}}
          onChange={(e) => {
            setupdateduser(prevState => ({
              ...prevState,
              [e.target.name]: e.target.value
            }));
          }}
        />
        Phonenumber:
        <input
          type="text"
          className="form-control"
          placeholder="Phone Number"
          name="phonenumber"
          value={updateduser.phonenumber}
          required
          // onChange={(e)=>{setupdateduser({phonenumber: e.target.value})}}
          onChange={(e) => {
            setupdateduser(prevState => ({
              ...prevState,
              [e.target.name]: e.target.value
            }));
          }}
        />
        isEmployee:
         <input
          type="text"
          className="form-control"
          placeholder="Employee?"
          name="isEmployee"
          value={updateduser.isEmployee}
          required
          // onChange={(e)=>{setupdateduser({isEmployee: e.target.value})}}
          onChange={(e) => {
            setupdateduser(prevState => ({
              ...prevState,
              [e.target.name]: e.target.value
            }));
          }}
        />
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancel
        </Button>
        <Button variant="secondary" type = "submit" onClick={handleUpdateUser}>
          Update
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export function AddUsers() {
  const [loading, setloading] = useState(false);
  const [error, seterror] = useState();

  const [name, setname] = useState("");
  const [address, setaddress] = useState();
  const [username, setusername] = useState();
  const [email, setemail] = useState();
  const [password, setpassword] = useState();
  const [phonenumber, setphonenumber] = useState();
  const [cpassword, setcpassword] = useState();
  const [access, setaccess] = useState();

  async function addUser() {
    let adminaccess = false;
    let employeeaccess = false;
    if (access == "admin") {
      adminaccess = true;
    } else if (access == "employee") {
      employeeaccess = true;
    }
    let newuser = {};
    if (cpassword === password) {
      newuser = {
        name,
        address,
        username,
        email,
        password,
        phonenumber,
        isAdmin: adminaccess,
        isEmployee: employeeaccess,
      };
    } else {
      alert("Please fill up all the requirements!");
    }

    try {
      setloading(true);
      const response = await axios.post("/api/users/register", newuser);
      const result = response.data;
      // const result = await(axios.post('/api/rooms/addroom', newroom)).data
      console.log(result);
      Swal.fire("Congrats", "New User is added successfully", "success").then(
        (result) => {
          window.location.href = "/admin";
        }
      );
      setloading(false);
    } catch (error) {
      console.log(error);
      setloading(false);
      seterror(error);
      Swal.fire("Oops", "Something went wrong", "error");
    }
  }
  return (
    <div className="row-1">
      <h1>Add User</h1>
      {loading && <Loader />}
      <div className="col-mid-5">
        <label for="name">Name:</label>
        <input
          type="text"
          className="form-control"
          name="name"
          required
          value={name}
          onChange={(e) => {
            setname(e.target.value);
          }}
        />
        <label for="address">Address:</label>
        <input
          type="text"
          className="form-control"
          name="address"
          required
          value={address}
          onChange={(e) => {
            setaddress(e.target.value);
          }}
        />
        <label for="username">Username:</label>
        <input
          type="text"
          className="form-control"
          name="username"
          required
          value={username}
          onChange={(e) => {
            setusername(e.target.value);
          }}
        />
        <label for="email">Email:</label>
        <input
          type="email"
          className="form-control"
          name="email"
          required
          value={email}
          onChange={(e) => {
            setemail(e.target.value);
          }}
        />
        <label for="password">Password:</label>
        <input
          type="password"
          className="form-control"
          name="passwrod"
          required
          value={password}
          onChange={(e) => {
            setpassword(e.target.value);
          }}
        />
        <label for="cpassword">Confirm Password:</label>
        <input
          type="password"
          className="form-control"
          name="cpassword"
          required
          value={cpassword}
          onChange={(e) => {
            setcpassword(e.target.value);
          }}
        />
        <label for="phonenumber">Phonenumber:</label>
        <input
          type="text"
          className="form-control"
          name="phonenumber"
          required
          value={phonenumber}
          onChange={(e) => {
            setphonenumber(e.target.value);
          }}
        />
        <label for="access">Access:</label>
        <select
          className="form-control"
          name="access"
          required
          onChange={(e) => {
            setaccess(e.target.value);
          }}
        >
          <option value="" disabled selected>
            Seclect admin/employee/guest
          </option>
          <option value="admin">Admin</option>
          <option value="employee">Employee</option>
          <option value="guest">Guest</option>
        </select>

        <div className="text-right">
          <button className="btn btn-primary mt-2" onClick={addUser}>
            Add Button
          </button>
        </div>
      </div>
    </div>
  );
}
