import React, { useState, useEffect } from "react";
import { Tabs } from "antd";
import axios from "axios";
import Loader from "../components/Loader";
import Error from "../components/Error";
import Swal from "sweetalert2";
import { Modal, Button, Carousel } from "react-bootstrap";

const { TabPane } = Tabs;

function Adminscreen() {
  // useEffect(() => {
  //   if ((!JSON.parse(localStorage.getItem("currentuser")).isAdmin) || (!JSON.parse(localStorage.getItem("currentuser")).isEmployee)) {
  //     window.location.href = "/home";
  //   }
  // }, []);
  useEffect(() => {
    const currentUser = JSON.parse(localStorage.getItem("currentuser"));
    if (!currentUser || (!currentUser.isAdmin && !currentUser.isEmployee)) {
      window.location.href = "/home";
    }
  }, []);

  return (
    <div className="mt-3 ml-3 mr-3 bs">
      <h2 className="text-center" style={{ fontSize: "30px" }}>
        <b>Admin Panel</b>
      </h2>
      <Tabs defaultActiveKey="1">
        <TabPane tab="Bookings" key="1">
          <Bookings />
        </TabPane>
        <TabPane tab="Manage Rooms" key="2">
          {/* <Rooms /> */}
          <Tabs defaultActiveKey="1">
            <TabPane tab="Show Rooms" key="1">
              <Rooms />
            </TabPane>
            <TabPane tab="Add Rooms" key="2">
              <AddRooms />
            </TabPane>
          </Tabs>
        </TabPane>
        {JSON.parse(localStorage.getItem("currentuser")).isAdmin && (
           <TabPane tab="Manage Users" key="4">
           <Tabs defaultActiveKey="1">
             <TabPane tab="Show Users" key="1">
               <Users />
             </TabPane>
             <TabPane tab="Add Users" key="2">
               <AddUsers />
             </TabPane>
           </Tabs>
         </TabPane>
        )}
      </Tabs>
    </div>
  );
}

export default Adminscreen;

export function Bookings() {
  const [bookings, setbookings] = useState([]);
  const [loading, setloading] = useState(true);
  const [error, seterror] = useState();

  useEffect(() => {
    async function getAllBookings() {
      try {
        const response = await axios.get("http://localhost:5000/api/bookings/getallbookings");
        const data = response.data;
        setbookings(data);
        setloading(false);
      } catch (error) {
        console.log(error);
        setloading(false);
        seterror(error);
      }
    }
    getAllBookings();
  }, []);

  return (
    <div className="row">
      <div className="col-md-12">
        <h1>Bookings</h1>
        {loading && <Loader />}

        <table className="table table-bordered table table-hover table table-striped">
          <thead className="bs thead-dark">
            <tr>
              <th>Booking Id</th>
              <th>User Id</th>
              <th>Room</th>
              <th>From</th>
              <th>To</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {bookings.length &&
              bookings.map((booking) => {
                return (
                  <tr>
                    <td>{booking._id}</td>
                    <td>{booking.userid}</td>
                    <td>{booking.room}</td>
                    <td>{booking.fromdate}</td>
                    <td>{booking.todate}</td>
                    <td>{booking.status}</td>
                  </tr>
                );
              })}
          </tbody>
        </table>

        {bookings.length && (
          <h1>There are total of {bookings.length} bookings</h1>
        )}
      </div>
    </div>
  );
}

export function Rooms() {
  const [rooms, setrooms] = useState([]);
  const [loading, setloading] = useState(true);
  const [error, seterror] = useState();

  // const [update, setupdate] = useState(false)
  const [updateState, setUpdateState] = useState(-1);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [show, setShow] = useState(false);

  function handleEdit(id) {
    setUpdateState(id);
  }

  useEffect(() => {
    async function getAllRooms() {
      try {
        const response = await axios.get("http://localhost:5000/api/rooms/getallrooms");
        const data = response.data;
        setrooms(data);
        setloading(false);
      } catch (error) {
        console.log(error);
        setloading(false);
        seterror(error);
      }
    }
    getAllRooms();
  }, []);

  function deleteRoom(roomid) {
    axios.delete(`http://localhost:5000/api/rooms/deleteroom/${roomid}`)
      .then((response) => {
        console.log(response.data);
        // update the users list in your state
        Swal.fire("Congrats", "Room is deleted successfully", "success").then(
          (result) => {
            setrooms(rooms.filter(room => room._id !== roomid));
          });
        

      })
      .catch((error) => {
        console.log(error);
        Swal.fire("Oops", "Something went wrong", "error");
      });
  }

  return (
    <div className="row">
      <div className="col-md-12">
        <h1>Rooms</h1>
        {loading && <Loader />}

        <table className="table table-bordered table table-hover table table-striped">
          <thead className="bs thead-dark">
            <tr>
              <th>Room Id</th>
              <th>Name</th>
              <th>Type</th>
              <th>Rent Per Day</th>
              <th>Max Count</th>
              <th>Phone Number</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {rooms.length &&
              rooms.map((room) => {
                return updateState === room._id ? (
                  <EditRoom
                    room={room}
                    rooms={rooms}
                    setrooms={setrooms}
                    show={show}
                    handleClose={handleClose}
                  />
                ) : (
                  <tr>
                    <td>{room._id}</td>
                    <td>{room.name}</td>
                    <td>{room.type}</td>
                    <td>{room.rentperday}</td>
                    <td>{room.maxcount}</td>
                    <td>{room.phonenumber}</td>
                    <td>
                      <button class="btn btn-primary" onClick={() => {
                          handleEdit(room._id);
                          handleShow();
                        }}>
                        Update
                      </button>
                      <button class="btn btn-primary" onClick={() => deleteRoom(room._id)}>Delete</button>
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export function AddRooms() {
  const [loading, setloading] = useState(false);
  const [error, seterror] = useState();

  const [name, setname] = useState("");
  const [rentperday, setrentperday] = useState();
  const [maxcount, setmaxcount] = useState();
  const [description, setdescription] = useState();
  const [phonenumber, setphonenumber] = useState();
  const [type, settype] = useState();
  const [imageurl1, setimageurl1] = useState();
  const [imageurl2, setimageurl2] = useState();
  const [imageurl3, setimageurl3] = useState();
  console.log(
    name,
    rentperday,
    maxcount,
    description,
    phonenumber,
    type,
    imageurl1,
    imageurl2,
    imageurl3
  );

  async function addRoom() {
    let newroom = {};
    if (
      name &&
      rentperday &&
      maxcount &&
      description &&
      phonenumber &&
      imageurl1 &&
      imageurl2 &&
      imageurl3
    ) {
      newroom = {
        name,
        rentperday,
        maxcount,
        description,
        phonenumber,
        type,
        imageurls: [imageurl1, imageurl2, imageurl3],
      };
    } else {
      alert("Please fill up all the requirements!");
    }

    try {
      setloading(true);
      const response = await axios.post("http://localhost:5000/api/rooms/addroom", newroom);
      const result = response.data;
      // const result = await(axios.post('/api/rooms/addroom', newroom)).data
      console.log(result);
      Swal.fire("Congrats", "New room is added successfully", "success").then(
        (result) => {
          window.location.href = "/home";
        }
      );
      setloading(false);
    } catch (error) {
      console.log(error);
      setloading(false);
      seterror(error);
      Swal.fire("Oops", "Something went wrong", "error");
    }
  }
  return (
    <div className="row-1">
      <h1>Add Rooms</h1>
      {loading && <Loader />}
      <div className="col-mid-5">
        <label for="name">Room name:</label>
        <input
          type="text"
          className="form-control"
          name="name"
          required
          value={name}
          onChange={(e) => {
            setname(e.target.value);
          }}
        />
        <label for="rent">Rent per day:</label>
        <input
          type="text"
          className="form-control"
          name="rent"
          required
          value={rentperday}
          onChange={(e) => {
            setrentperday(e.target.value);
          }}
        />
        <label for="maxcount">Max Count:</label>
        <input
          type="text"
          className="form-control"
          name="maxcount"
          required
          value={maxcount}
          onChange={(e) => {
            setmaxcount(e.target.value);
          }}
        />
        <label for="description">Descrition:</label>
        <input
          type="text"
          className="form-control"
          name="description"
          required
          value={description}
          onChange={(e) => {
            setdescription(e.target.value);
          }}
        />
        <label for="phonenumber">Phone Number:</label>
        <input
          type="text"
          className="form-control"
          name="phonenumber"
          required
          value={phonenumber}
          onChange={(e) => {
            setphonenumber(e.target.value);
          }}
        />
      </div>
      <div classname="col-mid-5">
        <label for="type">Type:</label>
        <select
          className="form-control"
          name="type"
          required
          onChange={(e) => {
            settype(e.target.value);
          }}
        >
          <option value="" disabled selected>
            Select room type
          </option>
          <option value="Delux">Delux</option>
          <option value="Non-Delux">Non-Delux</option>
        </select>
        <label for="imgeurl1">Image URL 1:</label>
        <input
          type="text"
          className="form-control"
          name="imageurl1"
          required
          value={imageurl1}
          onChange={(e) => {
            setimageurl1(e.target.value);
          }}
        />
        <label for="imgeurl2">Image URL 2:</label>
        <input
          type="text"
          className="form-control"
          name="imageurl2"
          required
          value={imageurl2}
          onChange={(e) => {
            setimageurl2(e.target.value);
          }}
        />
        <label for="imgeurl3">Image URL 3:</label>
