export function Users() {
  const [users, setusers] = useState([]);
  const [loading, setloading] = useState(true);
  const [error, seterror] = useState();

  const [updateState, setUpdateState] = useState(-1);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [show, setShow] = useState(false);

  useEffect(() => {
    async function getAllUsers() {
      try {
        const response = await axios.get("http://localhost:5000/api/users/getallusers");
        const data = response.data;
        setusers(data);
        setloading(false);
      } catch (error) {
        console.log(error);
        setloading(false);
        seterror(error);
      }
    }
    getAllUsers();
  }, []);

  function handleEdit(id) {
    setUpdateState(id);
  }
  function deleteUser(userid) {
    axios.delete(`http://localhost:5000/api/users/deleteuser/${userid}`)
      .then((response) => {
        console.log(response.data);
        // update the users list in your state
        Swal.fire("Congrats", "User is deleted successfully", "success").then(
          (result) => {
            setusers(users.filter(user => user._id !== userid));
          });
        

      })
      .catch((error) => {
        console.log(error);
        Swal.fire("Oops", "Something went wrong", "error");
      });
  }

  return (
    <div className="row">
      <div className="col-md-12">
        <h1>Users</h1>
        {loading && <Loader />}

        <table className="table table-bordered table table-hover table table-striped">
          <thead className="bs thead-dark">
            <tr>
              <th>Users Id</th>
              <th>Name</th>
              <th>Email</th>
              <th>Employee</th>
              <th>Admin</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.length &&
              users.map((user) => {
                return updateState === user._id ? (
                  <EditUser
                    user={user}
                    users={users}
                    setusers={setusers}
                    show={show}
                    handleClose={handleClose}
                  />
                ) : (
                  <tr>
                    <td>{user._id}</td>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.isEmployee ? "Yes" : "No"}</td>
                    <td>{user.isAdmin ? "Yes" : "No"}</td>
                    <td>
                      <button
                        class="btn btn-primary"
                        onClick={() => {
                          handleEdit(user._id);
