import React, { useState, useEffect } from "react";
import axios from "axios";
import Room from '../components/Room';
import Loader from "../components/Loader";
import Error from "../components/Error";
import moment from "moment";
import 'antd/dist/reset.css';
import { DatePicker, Space } from 'antd';
// import { useNavigate } from 'react-router-dom';



const { RangePicker } = DatePicker;

const Homescreen = () => {

  const [rooms, setRooms] = useState([]);
  const [loading, setloading] = useState(false);
  const [error, seterror] = useState(false);

  const[sdate, setfromdate] = useState();
  const[edate, settodate] = useState();
  // const [fromdate, setfromdate] = useState(moment().format("DD-MM-YYYY"));
  // const [todate, settodate] = useState(moment().add(1, "day").format("DD-MM-YYYY"));
  let fromdate = null;
  let todate = null;
  if(sdate && edate){
    fromdate = moment(sdate).format('DD-MM-YYYY');
    todate = moment(edate).add(1, 'day').format('DD-MM-YYYY');
    console.log(fromdate, todate);
  }
 

  useEffect(() => {
    async function getResults() {
      try {
        setloading(true);
        const data = (await axios("/api/rooms/getallrooms")).data;
        setRooms(data);
        setloading(false);
      } catch (error) {
        seterror(true);
        console.log(error);
        setloading(false);
      }
    }
    getResults();
  }, []);


  const filterByDate = (dates) => {
    if (dates) {
      // setfromdate(moment(dates[0]).format("DD-MM-YYYY"));
      // settodate(moment(dates[1]).format("DD-MM-YYYY"));
      const from_date = new Date(dates[0]).toLocaleDateString('en-GB');
      const to_date = new Date(dates[1]).toLocaleDateString('en-GB');

      // setfromdate(dates[0]);
      // settodate(dates[1]);
      setfromdate(from_date.replace(/\//g, '-'));
      settodate(to_date.replace(/\//g, '-'));

    }else{
      setfromdate(null);
      settodate(null);
    }
  };
  
  

  return (
    <div className="container">

      <div className="row mt-5">
        <div className="col-md-3">
        {/* <RangePicker format='DD-MM-YYYY' onChange={filterByDate}/> */}
        <RangePicker format="DD-MM-YYYY" onChange={filterByDate} />
        </div>
      </div>



      <div className="row justify-content-center mt-5">
        {loading ? (
          (<Loader />)
        ) : rooms.length >1 ? (
          rooms.map((room) => {
            return <div className="col-md-9 mt-3"> <Room room={room} fromdate={fromdate} todate={todate}/></div>;
          })
        ) : (
          (<Error />)
        )}
      </div>
    </div>
  );
}

export default Homescreen;

