import React, { useState, useEffect } from "react";
import { Tabs } from "antd";
import axios from "axios";
import Loader from "../components/Loader";
import Error from "../components/Error";

const { TabPane } = Tabs;

function Profilescreen() {
  const user = JSON.parse(localStorage.getItem("currentuser"));

  useEffect(() => {
    if (!user) {
      window.location.href = "/login";
    }
  }, []);

  return (
    <div className="ml-3 mt-3">
      <Tabs defaultActiveKey="1">
        <TabPane tab="Profile" key="1">
          <h1>My Profile</h1>
          <br />
          <h1>Name: {user.name}</h1>
          <h1>Email: {user.email}</h1>
          <h1>isAdmin: {user.isAdmin ? "YES" : "NO"}</h1>
        </TabPane>
        <TabPane tab="Bookings" key="2">
          <MyBookings />
        </TabPane>
      </Tabs>
    </div>
  );
}

export default Profilescreen;

export function MyBookings() {
  const user = JSON.parse(localStorage.getItem("currentuser"));
  const [bookings, setbookings] = useState([])
  const [loading, setloading] = useState(false);
  const [error, seterror] = useState();

  useEffect(() => {
    async function getMyBookings(){
        try {
            // const rooms = await axios.post('/api/getbookingsbyuserid', {userid: user._id}).data
            // console.log(rooms)
            setloading(true)
            const response = await axios.post("/api/bookings/getbookingsbyuserid", {
              userid: user._id,
            });
            const data = response.data;
            console.log(data);
            setbookings(data)
            setloading(false)
          } catch (error) {
            console.log(error);
            setloading(false)
            seterror(error)
          }
    }
    getMyBookings();
  }, []);

  return(
    <div>
        <div className="row">
            <div className="col-mid-6">
                {loading && (<Loader />)}
                {bookings && (bookings.map(booking=>{
                   return <div>
                    <h1>{booking.room}</h1>
                    <h1>BookingId : {booking._id}</h1>
                    <h1>CheckIn : {booking.fromdate}</h1>
                    <h1>CheckOut : {booking.todate}</h1>
                    <h1>Amount : {booking.totalamount}</h1>
                    <h1>Status : {booking.status == 'booked' ? 'CONFIRMED': 'CANCELLED'}</h1>
                    </div>
                }))}
            </div>
        </div>
    </div>
  );
}
