import React, { useState, useEffect } from "react";
import { Tabs } from "antd";
import axios from "axios";
import Loader from "../components/Loader";
import Error from "../components/Error";
import Swal from 'sweetalert2'

const { TabPane } = Tabs;

function Adminscreen() {

  useEffect(() => {
    if(!JSON.parse(localStorage.getItem("currentuser")).isAdmin){
      window.location.href='/home'
    }
  }, [])


  return (
    <div className="mt-3 ml-3 mr-3 bs">
      <h2 className="text-center" style={{ fontSize: "30px" }}>
        <b>Admin Panel</b>
      </h2>
      <Tabs defaultActiveKey="1">
        <TabPane tab="Bookings" key="1">
          <Bookings />
        </TabPane>
        <TabPane tab="Rooms" key="2">
          <Rooms />
        </TabPane>
        <TabPane tab="Add Rooms" key="3">
          <h1>Add Rooms</h1>
        </TabPane>
        <TabPane tab="Users" key="4">
          <Users />
        </TabPane>
      </Tabs>
    </div>
  );
}

export default Adminscreen;

export function Bookings() {
  const [bookings, setbookings] = useState([]);
  const [loading, setloading] = useState(true);
  const [error, seterror] = useState();

  useEffect(() => {
    async function getAllBookings() {
      try {
        const response = await axios.get("/api/bookings/getallbookings");
        const data = response.data;
        setbookings(data);
        setloading(false);
      } catch (error) {
        console.log(error);
        setloading(false);
        seterror(error);
      }
    }
    getAllBookings();
  }, []);

  return (
    <div className="row">
      <div className="col-md-12">
        <h1>Bookings</h1>
        {loading && <Loader />}

        <table className="table table-bordered table table-hover table table-striped">
          <thead className="bs thead-dark">
            <tr>
              <th>Booking Id</th>
              <th>User Id</th>
              <th>Room</th>
              <th>From</th>
              <th>To</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {bookings.length &&
              bookings.map((booking) => {
                return (
                  <tr>
                    <td>{booking._id}</td>
                    <td>{booking.userid}</td>
                    <td>{booking.room}</td>
                    <td>{booking.fromdate}</td>
                    <td>{booking.todate}</td>
                    <td>{booking.status}</td>
                  </tr>
                );
              })}
          </tbody>
        </table>

        {bookings.length && (
          <h1>There are total of {bookings.length} bookings</h1>
        )}
      </div>
    </div>
  );
}

export function Rooms(){
    const [rooms, setrooms] = useState([])
    const [loading, setloading] = useState(true);
    const [error, seterror] = useState();

    const[name, setname] = useState('')
    const[rentperday, setrentperday] = useState()
    const[maxcount, setmaxcount] = useState()
    const[description, setdescription] = useState()
    const[phonenumber, setphonenumber] = useState()
    const[type, settype] = useState()
    const[imageurl1, setimageurl1] = useState()
    const[imageurl2, setimageurl2] = useState()
    const[imageurl3, setimageurl3] = useState()
    console.log(name,rentperday,maxcount,description,phonenumber,type,imageurl1,imageurl2,imageurl3);

    // const [selectedRow, setSelectedRow] = useState(null);

    // async function addRoom(){
    //   let newroom = {};
    //   if(name && rentperday && maxcount && description && phonenumber && imageurl1 && imageurl2 && imageurl3){
    //     newroom = {
    //       name,
    //       rentperday,
    //       maxcount,
    //       description,
    //       phonenumber,
    //       type,
    //       imageurls:[imageurl1, imageurl2, imageurl3]
    //     } 
    //   } else{
    //     alert("Please fill up all the requirements!")
    //   }
     
    //   try {
    //     setloading(true)
    //     const response = await axios.post('/api/rooms/addroom', newroom);
    //     const result = response.data;
    //     // const result = await(axios.post('/api/rooms/addroom', newroom)).data
    //     console.log(result)
    //     Swal.fire('Congrats', "New room is added successfully", 'success').then(result =>{
    //       window.location.href = '/home'
    //     })
    //     setloading(false)
    //   } catch (error) {
    //     console.log(error)
    //     setloading(false)
    //     seterror(error)
    //     Swal.fire('Oops', "Something went wrong", 'error')
    //   }
    // }


    useEffect(() => {
        async function getAllRooms(){

            try {
                const response = await axios.get('/api/rooms/getallrooms');
                const data = response.data;
                setrooms(data)
                setloading(false)
            } catch (error) {
                console.log(error)
                setloading(false)
                seterror(error)
            }
        }
        getAllRooms();
    }, []);


    return(
        <div className='row'>
            <div className='col-md-12'>
                <h1>Rooms</h1>
                {loading && (<Loader />)}

                <table className='table table-bordered table table-hover table table-striped'>
                    <thead className='bs thead-dark'>
                        <tr>
                            <th>Room Id</th>
                            <th>Name</th>
                            <th>Descrition</th>
                            <th>Type</th>
                            <th>Rent Per Day</th>
                            <th>Max Count</th>
                            <th>Phone Number</th>
                        </tr>
                    </thead>
                    <tbody>
                        {rooms.length && (rooms.map(room=>{
                            return <tr>
                                <td>{room._id}</td>
                                <td>{room.name}</td>
                                <td>{room.description}</td>
                                <td>{room.type}</td>
                                <td>{room.rentperday}</td>
                                <td>{room.maxcount}</td>
                                <td>{room.phonenumber}</td>
                            </tr>
                        }))}
                    </tbody>
                </table>
                <br />
                <br />
                <br />
                <div className="bs">
                <div className="row-1">
                  {loading && (<Loader />)}
                <div className = "col-mid-5">
                <label for="name">Room name:</label>
                <input type="text" className="form-control" name="name" required
                value={name} onChange={(e)=>{setname(e.target.value)}} />
                <label for="rent">Rent per day:</label>
                <input type="text" className="form-control" name="rent" required
                value={rentperday} onChange={(e)=>{setrentperday(e.target.value)}}/>
                <label for="maxcount">Max Count:</label>
                <input type="text" className="form-control" name="maxcount" required
                value={maxcount} onChange={(e)=>{setmaxcount(e.target.value)}} />
                <label for="description">Descrition:</label>
                <input type="text" className="form-control" name="description" required
                value={description} onChange={(e)=>{setdescription(e.target.value)}}/>
                 <label for="phonenumber">Phone Number:</label>
                <input type="text" className="form-control" name="phonenumber" required
                value={phonenumber} onChange={(e)=>{setphonenumber(e.target.value)}}/>
                </div> 
                <div classname = "col-mid-5">
                  <label for="type">Type:</label>
                  <select className="form-control" name="type" required onChange={(e)=>{settype(e.target.value)}}>
                  <option value="" disabled selected>Select room type</option>
                    <option value="Delux">Delux</option>
                    <option value="Non-Delux">Non-Delux</option>
                  </select>
                  <label for="imgeurl1">Image URL 1:</label>
                <input type="text" className="form-control" name="imageurl1" required
                value={imageurl1} onChange={(e)=>{setimageurl1(e.target.value)}}/>
                <label for="imgeurl2">Image URL 2:</label>
                <input type="text" className="form-control" name="imageurl2" required
                value={imageurl2} onChange={(e)=>{setimageurl2(e.target.value)}}/>
                <label for="imgeurl3">Image URL 3:</label>
                <input type="text" className="form-control" name="imageurl3" required
                value={imageurl3} onChange={(e)=>{setimageurl3(e.target.value)}}/>
                <div className="text-right">
                  <button className="btn btn-primary mt-2" onClick={addRoom}>Add Button</button>
                </div>
                </div>  
                </div>             

                </div>              
            </div>
        </div>
    )
}

export function addRooms(){
  const [rooms, setrooms] = useState([])
  const [loading, setloading] = useState(true);
  const [error, seterror] = useState();

  const[name, setname] = useState('')
  const[rentperday, setrentperday] = useState()
  const[maxcount, setmaxcount] = useState()
  const[description, setdescription] = useState()
  const[phonenumber, setphonenumber] = useState()
  const[type, settype] = useState()
  const[imageurl1, setimageurl1] = useState()
  const[imageurl2, setimageurl2] = useState()
  const[imageurl3, setimageurl3] = useState()
  console.log(name,rentperday,maxcount,description,phonenumber,type,imageurl1,imageurl2,imageurl3);

  // const [selectedRow, setSelectedRow] = useState(null);

  async function addRoom(){
    let newroom = {};
    if(name && rentperday && maxcount && description && phonenumber && imageurl1 && imageurl2 && imageurl3){
      newroom = {
        name,
        rentperday,
        maxcount,
        description,
        phonenumber,
        type,
        imageurls:[imageurl1, imageurl2, imageurl3]
      } 
    } else{
      alert("Please fill up all the requirements!")
    }
   
    try {
      setloading(true)
      const response = await axios.post('/api/rooms/addroom', newroom);
      const result = response.data;
      // const result = await(axios.post('/api/rooms/addroom', newroom)).data
      console.log(result)
      Swal.fire('Congrats', "New room is added successfully", 'success').then(result =>{
        window.location.href = '/home'
      })
      setloading(false)
    } catch (error) {
      console.log(error)
      setloading(false)
      seterror(error)
      Swal.fire('Oops', "Something went wrong", 'error')
    }
  }
  return(
      <div className='row'>
          <div className='col-md-12'>
              <h1>Rooms</h1>
              {loading && (<Loader />)}
              <div className="bs">
              <div className="row-1">
                {loading && (<Loader />)}
              <div className = "col-mid-5">
              <label for="name">Room name:</label>
              <input type="text" className="form-control" name="name" required
              value={name} onChange={(e)=>{setname(e.target.value)}} />
              <label for="rent">Rent per day:</label>
              <input type="text" className="form-control" name="rent" required
              value={rentperday} onChange={(e)=>{setrentperday(e.target.value)}}/>
              <label for="maxcount">Max Count:</label>
              <input type="text" className="form-control" name="maxcount" required
              value={maxcount} onChange={(e)=>{setmaxcount(e.target.value)}} />
              <label for="description">Descrition:</label>
              <input type="text" className="form-control" name="description" required
              value={description} onChange={(e)=>{setdescription(e.target.value)}}/>
               <label for="phonenumber">Phone Number:</label>
              <input type="text" className="form-control" name="phonenumber" required
              value={phonenumber} onChange={(e)=>{setphonenumber(e.target.value)}}/>
              </div> 
              <div classname = "col-mid-5">
                <label for="type">Type:</label>
                <select className="form-control" name="type" required onChange={(e)=>{settype(e.target.value)}}>
                <option value="" disabled selected>Select room type</option>
                  <option value="Delux">Delux</option>
                  <option value="Non-Delux">Non-Delux</option>
                </select>
                <label for="imgeurl1">Image URL 1:</label>
              <input type="text" className="form-control" name="imageurl1" required
              value={imageurl1} onChange={(e)=>{setimageurl1(e.target.value)}}/>
              <label for="imgeurl2">Image URL 2:</label>
              <input type="text" className="form-control" name="imageurl2" required
              value={imageurl2} onChange={(e)=>{setimageurl2(e.target.value)}}/>
              <label for="imgeurl3">Image URL 3:</label>
              <input type="text" className="form-control" name="imageurl3" required
              value={imageurl3} onChange={(e)=>{setimageurl3(e.target.value)}}/>
              <div className="text-right">
                <button className="btn btn-primary mt-2" onClick={addRoom}>Add Button</button>
              </div>
              </div>  
              </div>             

              </div>              
          </div>
      </div>
  )
}


export function Users(){
    const [users, setusers] = useState([])
    const [loading, setloading] = useState(true);
    const [error, seterror] = useState();

    useEffect(() => {
        async function getAllUsers(){

            try {
                const response = await axios.get('/api/users/getallusers');
                const data = response.data;
                setusers(data)
                setloading(false)
            } catch (error) {
                console.log(error)
                setloading(false)
                seterror(error)
            }
        }
        getAllUsers();
    }, []);


    return(
        <div className='row'>
            <div className='col-md-12'>
                <h1>Users</h1>
                {loading && (<Loader />)}

                <table className='table table-bordered table table-hover table table-striped'>
                    <thead className='bs thead-dark'>
                        <tr>
                            <th>Users Id</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Employee</th>
                            <th>Admin</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.length && (users.map(user=>{
                            return <tr>
                                <td>{user._id}</td>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{user.isEmployee ? 'Yes' : 'No'}</td>
                                <td>{user.isAdmin ? 'Yes' : 'No'}</td>
                            </tr>
                        }))}
                    </tbody>
                </table>
            </div>
        </div>
    )
}
